import user from '../views/user.vue'
import mall from '../views/mall.vue'
import PageOne from '../views/PageOne.vue'
import PageTwo from '../views/PageTwo.vue'

Vue.use(VueRouter)

const routes = [

  {
    path: '/',
    name: 'about',
    // component: AboutView
