export default{
    state:{
        isCollapse:false,
        tabList:[
            {
            path: '/home',
            name: 'home',
            label: '首页',
            icon: 's-home',
            url: 'Home/Home'
        }]
    },
    mutations:{
        changeCollapse(state){
            state.isCollapse=!state.isCollapse;
        },
        selectMenu(state,val){//state是vuex的state对象，val是触发selectMenu时传过来的item
            // console.log("==========state",state)
            console.log("========val",val)
            // 判断添加的数据是否为首页
            // if (val.name !== 'home') {
            //     const index = state.tabList.findIndex(item => item.name === val.name)
            //     // 如果不存在
            //     if (index === -1) {
            //         state.tabList.push(val)
            //     }
            // }
            state.tabList=[];
            state.tabList.unshift({
                path: '/home',
                name: 'home',
                label: '首页',
                icon: 's-home',
                url: 'Home/Home'
            });
            if (val.name !== 'home') {
                state.tabList.push(val)
                if(val.children){
                    state.tabList.push(val.children)
                }
            }
            
        }
    }
}
