import PageTwo from '../views/PageTwo.vue'

Vue.use(VueRouter)

const routes = [

  {
    path: '/',
    name: 'about',
    // component: AboutView
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue'),
    redirect:"/home",
    children: [
      { path: '/home',name: 'home',meta:{label:"首页"},component: HomeView},
      { path: '/user',name: 'user',meta:{label:"用户管理"},component: user},
      { path: '/mall',name: 'user',meta:{label:"商品管理"},component: mall},
      { path: '/Page1',name: 'user',meta:{label:"页面1"},component: PageOne},
      { path: '/Page2',name: 'user',meta:{label:"页面2"},component: PageTwo}
    ]
  },

]

const router = new VueRouter({
  routes
})

export default router
